import React, { useRef, useState, useEffect } from 'react'
import { Form, Button, Card, Alert } from 'react-bootstrap'
import { useAuth } from '../contexts/AuthContext'
import { Link, useHistory } from 'react-router-dom'

export default function UpdateProfile() {

    const emailRef = useRef()
    const passwordRef = useRef()
    const passwordConfirmRef = useRef()
    const history = useHistory()

    const { currentUser, updateEmail, updatePassword } = useAuth()

    const [error, setError] = useState('')

    //loading state controls the 'Sign Up' button and prevent adding mulitiple users to the database with muiltiple clicks on the button   
    const [loading, setLoading] = useState(false)

    function unlisten (address) {
        history.push(address)
}

    useEffect( () => {
        return unlisten
    }, [])


    async function handleSubmit(e) {

        e.preventDefault()

        if (passwordRef.current.value !==
            passwordConfirmRef.current.value) {
                return setError('Passwords do not match')
            }

        const promises = []

        if (emailRef.current.value !== currentUser)

        try {
            //clears up the current error message state to possibly catch a new error in a catch-block below
            setError('')
            setLoading(true)
                // await signup(emailRef.current.value, passwordRef.current.value)
                // unlisten('/')
 //           history.push("/")
        } catch (error) {
            console.log(error)
            setError(error.message)
        }

        setLoading(false)
    }

    return (
     <>
       <Card>
           <Card.Body>
               <h2 className="text-center mb-4">Update Profile</h2>
               {error && <Alert variant="danger">{`Failed: ${error}`}</Alert>}
               <Form onSubmit={handleSubmit}>
                   <Form.Group id="email">
                       <Form.Label>Email</Form.Label>
                       <Form.Control type="email" ref={emailRef} required 
                       defaultValue={ currentUser.email } />
                   </Form.Group>

                   <Form.Group id="password">
                       <Form.Label>Password</Form.Label>
                       <Form.Control type="password" ref={passwordRef}  
                       placeholder="Leave blank to keep the same"/>
                   </Form.Group>

                   <Form.Group id="password-confirm">
                       <Form.Label>Password Confirmation</Form.Label>
                       <Form.Control type="password-confirm" ref={passwordConfirmRef}  
                       placeholder="Leave blank to keep the same" />
                   </Form.Group>
                   <Button disabled={loading} className="w-100" type="submit">
                       Update
                   </Button>
               </Form>
           </Card.Body>


       </Card>   

        <div className="w-100 text-center mt-2"> 
          <Link to='/'>Cancel</Link>            
        </div>
     </>
    )
}
