import React, { useContext, useState, useEffect } from 'react'
import { auth } from '../firebase'


const AuthContext = React.createContext()

export function useAuth() {
    return useContext(AuthContext)
}

export function AuthProvider({ children }) {

    const [ currentUser, setCurrentUser] = useState()
    const [ loading, setLoading ] = useState(true)


    function signup(email, password) {
        return auth.createUserWithEmailAndPassword(email, password)
    }

    useEffect(() => {
        //firebase method onAuthStateChanged adds a listener to the page that listens for 'signup' events, which needs to be cleaned up after the component is unmounted
        const unsubscribe = auth.onAuthStateChanged( user => {
            setCurrentUser(user)
            setLoading(false)
        })
        // unsubscribe removes the listener added to the page by onAuthStateChanged when component unmounts as in everu 
        return unsubscribe
    }, [])

    const value = {
    currentUser,
    signup
}

    return (
        <AuthContext.Provider value={ value }>
            { !loading && children }
        </AuthContext.Provider>
    )
}
